import requests
from bs4 import BeautifulSoup
import json

url = 'https://books.toscrape.com/catalogue/page-2.html'
response = requests.get(url)

if response.status_code == 200:
    soup = BeautifulSoup(response.content, 'html.parser')
    products = soup.find_all('article', class_='product_pod')

    product_data = []
    for product in products:
        title = product.h3.a['title']
        product_url = 'https://books.toscrape.com/catalogue/' + product.h3.a['href']
        price = product.find('p', class_='price_color').text
        availability = product.find('p', class_='instock availability').text.strip()
        try:
            image_url = 'https://books.toscrape.com/' + product.find('img', class_='thumbnail')['src'].replace('../', '')
        except:
            image_url = 'N/A'
        product_data.append({
            'title': title,
            'product_url': product_url,
            'price': price,
            'availability': availability,
            'image_url': image_url
        })

    product_data_json = json.dumps(product_data, indent=4)
    return {'product_data_page_2_T002': product_data_json}
else:
    return {'product_data_page_2_T002': f'Request failed with status code {response.status_code}'}

